uint8 CT_PrintCode39(const char* pin){
	uint32 height;
	uint8 result;
	char _pin[13];

	sprintf(_pin, "*%s*", pin);

	TP_PrnSessionStart();
	height = CT_StringToCode39Bitmap(_pin, PrintBitMap);
	result = TP_PrnLogo(PrintBitMap,384,height);
	TP_PrnStep(8);
	TP_PrnSessionEnd();
	return result;
}

uint8 CT_PrintReceipt(const char* title, const char* summary, const char* text, uint8 company, uint8 type){
    uint8 result = 0;
	

	TP_ScrSetIcon(ICON_PRINTER, OPENICON);
    TP_PrnSessionStart();

	TP_PrnStep(3);

	if( !type ){
		switch(company){
			case CT_DIRECTV:
				result = TP_PrnLogo(dtvLogo, dtvLogoSize[0], dtvLogoSize[1]);
				break;
			case CT_MOVILNET:
				result = TP_PrnLogo(movilnetLogo, movilnetLogoSize[0], movilnetLogoSize[1]);
				break;
			case CT_BIGOTT:
				result = TP_PrnLogo(bigottLogo, bigottLogoSize[0], bigottLogoSize[1]);
				break;
		}
	}

	if( result != 0x00 ){
		TP_PrnSessionEnd();
		return result;
	}

	TP_PrnStep(1);
	
	//printing title
	TP_PrnFontSet(2);
	TP_PrnClearString();
    TP_PrnAddString((uint8*)"%s\n", title);
	result = TP_PrnString();
	if( result != 0x00 ){
		TP_PrnSessionEnd();
		return result;
	}
	//printing summary
	if( strlen(summary) > 0 ){
		TP_PrnFontSet(2);
		TP_PrnClearString();
		TP_PrnAddString((uint8*)"%s\n", summary);
		result = TP_PrnString();
		if( result != 0x00 ){
			TP_PrnSessionEnd();
			return result;
		}
	}
	//printing everything else
	TP_PrnFontSet(1);
	TP_PrnClearString();
    TP_PrnAddString((uint8*)"%s\n", text);
	result = TP_PrnString();
	if( result != 0x00 ){
		TP_PrnSessionEnd();
		return result;
	}
	TP_PrnStep(10);

    result = TP_PrnSessionEnd();
	if( result != 0x00 ){
		return result;
	}

    TP_ScrSetIcon(ICON_PRINTER, CLOSEICON);
	return result;
}

uint8 CT_PrintReceiptBarcode(const char* title, const char* summary, const char* text, uint8 company, const char* pin){
	uint8 result = CT_PrintReceipt(title, summary, text, company, 0);

	if(result == 0x00){
		TP_ScrCls();
		TP_ScrGotoxy(3, 0);
		TP_LcdPrintf((uint8*)"Imprimiendo codigo de barra...");
		CT_PrintCode39(pin);
	}
	return result;
}

uint8 CT_PrintReceiptClient(uint8 type, const char * companyName, uint16 companyId, const char * suscriptorNumber, const char * amount, const char * serialNumber, const char * pin, const char * systemTime){
	uint8 cTemp;
	char date[20];
	char pin_printable[PIN_PRINTABLE_LENGHT];
	char information[INF_LENGHT];
	char title[128];
	
	if(type == 0)
		sprintf(title, "RECARGA ELECTRONICA\nRECIBO");
	else
		sprintf(title, "PAGO DE FACTURA\nRECIBO");

	get_human_date( systemTime, date );	

	if( strlen(pin) > 10 ){
		format_pin(pin, pin_printable);
		sprintf(information, "COPIA - CLIENTE\nFecha: %s\nOperadora: %s\nNumero: %s\nMonto: %s Bs\nSerial: %s\nPIN: %s", date,  companyName, suscriptorNumber, amount, serialNumber, pin_printable);		
	}else{
		sprintf(information, "COPIA - CLIENTE\nFecha: %s\nOperadora: %s\nNumero: %s\nMonto: %s Bs\nSerial: %s\nPIN: %s", date,  companyName, suscriptorNumber, amount, serialNumber, pin);		
	}

	if( type == 1 ){
		cTemp = CT_PrintReceiptBarcode(title, "", information, companyId, pin);
	}else{
		cTemp = CT_PrintReceipt(title, "", information, companyId, 0);
	}

	return cTemp;
}

uint8 CT_PrintReceiptCompany(uint8 type, const char * companyName, uint16 companyId, const char * suscriptorNumber, const char * amount, const char * serialNumber, const char * systemTime){
	uint8 cTemp;
	char date[20];
	char information[INF_LENGHT];
	char title[128];
	char balanceString[15];
	char companyNameUpper[30] = "COMERCIO";

	if(type == 0){
		sprintf(title, "RECARGA ELECTRONICA\nRECIBO");
	}else{
		sprintf(title, "PAGO DE FACTURA\nRECIBO");
		to_upper(companyName, companyNameUpper);
	}

	get_human_date( systemTime, date );	
	point_to_comma_decimal(g_userData.balance, balanceString);

	sprintf(information, "COPIA - %s\nFecha: %s\nOperadora: %s\nNumero: %s\nMonto: %s Bs\nSerial: %s\nSaldo: %s Bs", companyNameUpper, date, companyName, suscriptorNumber, amount, serialNumber, balanceString);

	TP_ScrCls();
	TP_ScrGotoxy(3, 0);
    TP_LcdPrintf((uint8*)"Imprimiendo Copia Compania...");

	cTemp = CT_PrintReceipt(title, "", information, companyId, 1);

	return cTemp;
}

uint8 CT_PrintReceiptClientAndCompany(uint8 type, const char * companyName, uint16 companyId, const char * suscriptorNumber, const char * amount, const char * serialNumber, const char * pin, const char * systemTime){
	int32 cTemp;

	TP_ScrCls();
	TP_ScrGotoxy(3, 0);
    TP_LcdPrintf((uint8*)"Imprimiendo Copia Cliente...");

	cTemp = CT_PrintReceiptClient(type, companyName, companyId, suscriptorNumber, amount, serialNumber, pin, systemTime);   

	if( cTemp == 0x00 ){
		CT_RegisterPrintSuccess();

		TP_ScrCls();
		CT_Notify("Para continuar", 0);

		CT_PrintReceiptCompany(type, companyName, companyId, suscriptorNumber, amount, serialNumber, systemTime);   
	}
	return cTemp;
}

void CT_PrintLastTransaction(){
	char information[INF_LENGHT];
	SALE_INFO sale_info;
	SALE_INFO_FULL sale_info_full;
	char date[20];
	int32 cTemp;
	char amountString[10];
	char companyName[10];

	double amount;

	if( TP_FExist( "LAST" ) ){
		cTemp = CT_LastTransaction(&sale_info_full);
		if( cTemp == 0x00 ){
			amount = ISO8583DecimalTodecimalString(sale_info_full.isoAmount);
			sprintf(amountString, "%0.2f", amount);

			if( sale_info_full.type == 0 ){
				get_name_from_company_recharge(sale_info_full.serviceCompany, companyName);
			}else{
				get_name_from_company_payment(sale_info_full.serviceCompany, companyName);
			}
			
			cTemp = CT_PrintReceiptClientAndCompany(sale_info_full.type, companyName, sale_info_full.serviceCompany, sale_info_full.subscriber, amountString, sale_info_full.cSerialNo, sale_info_full.cPinNo, sale_info_full.topupDate);   	
		}
	}else{
		if( TP_FExist( "LOG" ) ){
			cTemp = CT_LastTransactionInLog(&sale_info);
			if( cTemp > 0x00 ){
				amount = ISO8583DecimalTodecimalString(sale_info.isoAmount);
				point_to_comma_decimal(amount, amountString);

				if( sale_info.type == 0 ){
					get_name_from_company_recharge(sale_info.serviceCompany, companyName);
				}else{
					get_name_from_company_payment(sale_info.serviceCompany, companyName);
				}
			
				cTemp = CT_PrintReceiptCompany(sale_info.type, companyName, sale_info.serviceCompany, sale_info.subscriber, amountString, sale_info.cSerialNo, sale_info.topupDate);  
			}else{
				CT_Notify("No hay transacciones para imprimir", 0);
			}
		}else{
			CT_Notify("No hay transacciones para imprimir", 0);
		}
	}
	if( cTemp != 0x00 ){
		TP_ScrCls();
		TP_ScrGotoxyEx(0, 0);
		TP_LcdPrintf((uint8*)"ERROR IMPRIMIENDO:0x%02X", cTemp);
		while (TP_Kbhit() != 0xFF){}
		TP_Kbflush();
	}
}